class Showcase_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get showcase by id
     */
    function get_showcase($id)
    {
        return $this->db->get_where('showcase',array('id'=>$id))->row_array();
    }
        
    /*
     * Get all showcase
     */
    function get_all_showcase()
    {
        $this->db->order_by('position', 'asc');
        return $this->db->get('showcase')->result_array();
    }
        
