     */
    function update_showcase($id,$params)
    {
        $this->db->where('id',$id);
        return $this->db->update('showcase',$params);
    }
    
    /*
     * function to delete showcase
     */
    function delete_showcase($id)
    {
        return $this->db->delete('showcase',array('id'=>$id));
    }
